import React, { Component } from 'react';
import Grid from '@material-ui/core/Grid';
import PropTypes from 'prop-types';

import Budget from '../components/budget/Budget';
import Profile from '../components/profile/Profile';
import ScreamSkeleton from '../util/ScreamSkeleton';

import { connect } from 'react-redux';
import { getBudgets } from '../redux/actions/dataActions';

class categories extends Component {
  componentDidMount() {
    const categoryId = this.props.match.params.categoryId;
    console.log("on categories")
    this.props.getCategories(categoryId);
  }
  render() {
    const { categories, loading } = this.props.data;
    let categoriesMarkup = !loading ? (
        categories.map((category) => <Budget key={category.categoryId} budget={category} />)
    ) : (
      <ScreamSkeleton />
    );
    return (
      <Grid container spacing={16}>
        <Grid item sm={8} xs={12}>
          {categoriesMarkup}
        </Grid>
        <Grid item sm={4} xs={12}>
          <Profile />
        </Grid>
      </Grid>
    );
  }
}

categories.propTypes = {
  getBudgets: PropTypes.func.isRequired,
  data: PropTypes.object.isRequired
};

const mapStateToProps = (state) => ({
  data: state.data
});

export default connect(
  mapStateToProps,
  { getBudgets }
)(categories);
