import {
  SET_BUDGETS,
  SET_CATEGORIES,
  LOADING_DATA,
  LIKE_SCREAM,
  UNLIKE_SCREAM,
  DELETE_BUDGET,
  SET_ERRORS,
  POST_EXPENSE,
  CLEAR_ERRORS,
  LOADING_UI,
  SET_SCREAM,
  STOP_LOADING_UI,
  SUBMIT_COMMENT,
  SET_EXPENSES
} from '../types';
import axios from 'axios';

// Get all screams
export const getBudgets = () => (dispatch) => {
  console.log("You are here")
  dispatch({ type: LOADING_DATA });
  axios
    .get('/budgets')
    .then((res) => {
      dispatch({
        type: SET_BUDGETS,
        payload: res.data
      });
    })
    .catch((err) => {
      dispatch({
        type: SET_BUDGETS,
        payload: []
      });
    });
};
// Get all categories by budget id
export const getCategories = (budgetId) => (dispatch) => {
  console.log("You are about to get categories")
  dispatch({ type: LOADING_DATA });
  axios
    .get(`/categories/${budgetId}`)
    .then((res) => {
      dispatch({
        type: SET_CATEGORIES,
        payload: res.data
      });
    })
    .catch((err) => {
      dispatch({
        type: SET_CATEGORIES,
        payload: []
      });
    });
};
// Get all categories by budget id
export const getExpenses = (categoryId) => (dispatch) => {
  dispatch({ type: LOADING_DATA });
  axios
    .get(`/expenses/${categoryId}`)
    .then((res) => {
      dispatch({
        type: SET_EXPENSES,
        payload: res.data
      });
    })
    .catch((err) => {
      dispatch({
        type: SET_EXPENSES,
        payload: []
      });
    });
};
export const getScream = (screamId) => (dispatch) => {
  dispatch({ type: LOADING_UI });
  axios
    .get(`/scream/${screamId}`)
    .then((res) => {
      dispatch({
        type: SET_SCREAM,
        payload: res.data
      });
      dispatch({ type: STOP_LOADING_UI });
    })
    .catch((err) => console.log(err));
};
// Post a scream
export const postExpense = (newExpense) => (dispatch) => {
  dispatch({ type: LOADING_UI });
  axios
    .post('/expense', newExpense)
    .then((res) => {
      dispatch({
        type: POST_EXPENSE,
        payload: res.data
      });
      dispatch(clearErrors());
    })
    .catch((err) => {
      dispatch({
        type: SET_ERRORS,
        payload: err.response.data
      });
    });
};
// Like a scream
export const likeScream = (screamId) => (dispatch) => {
  axios
    .get(`/scream/${screamId}/like`)
    .then((res) => {
      dispatch({
        type: LIKE_SCREAM,
        payload: res.data
      });
    })
    .catch((err) => console.log(err));
};
// Unlike a scream
export const unlikeScream = (screamId) => (dispatch) => {
  axios
    .get(`/scream/${screamId}/unlike`)
    .then((res) => {
      dispatch({
        type: UNLIKE_SCREAM,
        payload: res.data
      });
    })
    .catch((err) => console.log(err));
};
// Submit a comment
export const submitComment = (screamId, commentData) => (dispatch) => {
  axios
    .post(`/scream/${screamId}/comment`, commentData)
    .then((res) => {
      dispatch({
        type: SUBMIT_COMMENT,
        payload: res.data
      });
      dispatch(clearErrors());
    })
    .catch((err) => {
      dispatch({
        type: SET_ERRORS,
        payload: err.response.data
      });
    });
};
export const deleteBudget = (budgetId) => (dispatch) => {
  axios
    .delete(`/budget/${budgetId}`)
    .then(() => {
      dispatch({ type: DELETE_BUDGET, payload: budgetId });
    })
    .catch((err) => console.log(err));
};

export const getUserData = (userHandle) => (dispatch) => {
  dispatch({ type: LOADING_DATA });
  axios
    .get(`/user/${userHandle}`)
    .then((res) => {
      dispatch({
        type: SET_BUDGETS,
        payload: res.data.screams
      });
    })
    .catch(() => {
      dispatch({
        type: SET_BUDGETS,
        payload: null
      });
    });
};

export const clearErrors = () => (dispatch) => {
  dispatch({ type: CLEAR_ERRORS });
};
